mapper.select:

select IMPORTDATE from t_import_record 
where filetype=#{type} and importdate<=#{date}
group by importdate order by importdate desc limit 1

select a.managerid as managerid, a.percent as percent , b.accountid as accountid, b.accountname as accountname, b.branchid as branchid, c.gendate, c.bal as bal, c.yearavg as yeardayavg, c.m1, c.m2, c.m3, c.m4, c.m5, c.m6, c.m7, c.m8, c.m9, c.m10, c.m11, c.m12 from t_account_base b left join t_accountbal_record c on b.accountid=c.accountid left join t_manager_account a on b.accountid=a.accountid where 1=1
and a.managerid=#{managerid}
and b.accounttype=#{accounttype}
and b.branchid=#{branchid}
and b.accountid like CONCAT(CONCAT('%',#{accountid}),'%')
and c.gendate=#{date}
group by b.accountid

select a.managerid as managerid, a.percent as percent , b.accountid as accountid, b.accountname as accountname, b.branchid as branchid, c.tranamt as bal, c.startdate as startdate, c.enddate as enddate from t_account_base b inner join t_finance_detail c on b.accountid=c.accountid left join t_manager_account a on b.accountid=a.accountid left join T_FINANCE_PRODUCT_TYPE d on c.productid=d.id where 1=1 and d.isjsckye=1
and (not (c.enddate<#{startdate} or c.startdate>#{enddate}))
and a.managerid=#{managerid}
and b.accounttype=#{accounttype}

select a.managerid as managerid, a.percent as percent , b.accountid as accountid, b.accountname as accountname, b.branchid as branchid, c.tranamt as bal, c.startdate as startdate, c.enddate as enddate from t_account_base b inner join t_finance_detail c on b.accountid=c.accountid left join t_manager_account a on b.accountid=a.accountid left join T_FINANCE_PRODUCT_TYPE d on c.productid=d.id where 1=1 and d.isjsrj=1
and (not (c.enddate<#{startdate} or c.startdate>#{enddate}))
and a.managerid=#{managerid}
and b.accounttype=#{accounttype}

select a.managerid as managerid, a.percent as percent , b.accountid as accountid, b.accountname as accountname, b.branchid as branchid, c.tranamt as bal, c.startdate as startdate, c.enddate as enddate from t_account_base b inner join T_ACCOUNT_PERSON_PLEDGE c on b.accountid=c.accountid left join t_manager_account a on b.accountid=a.accountid where 1=1 and b.accounttype=5 and b.subaccttype=1
and a.managerid=#{managerid}
and not (c.enddate<#{startdate} or c.startdate>#{enddate})

select a.accountid , a.accountname, d.branchname, b.tranamt, b.startdate, b.enddate from t_account_base a, T_ACCOUNT_PERSON_PLEDGE b ,
t_manager_account c ,
sys_branch d where a.accounttype='5' and a.subaccttype='1' and a.accountid=b.accountid and a.branchid=d.branchid
and c.accountid=a.accountid and c.managerid=#{managerid}
and a.branchid=#{branchid}
and a.accountid like CONCAT(CONCAT('%',#{accountid}),'%')

      	select 
 			count(*) as cntamt,
 			sum(b.tranamt*c.percent) as totalamt
	 	from 
			t_account_base a
 			inner join t_finance_detail b on a.accountid=b.accountid
			left join t_manager_account c on a.accountid=c.accountid
			left join  t_finance_product_type d  on b.productid=d.id
		where 1=1

         		and a.accounttype in 

            		#{item}



    		    and   c.managerid=#{managerid} 


			    and   b.productid=#{productid}


			    and   b.productbatch like CONCAT(CONCAT('%',#{productbatch}),'%')


			    and   b.startdate>=#{startdate}



			    	 and   b.enddate <=#{enddate}



			    and b.channel=#{channel}


			    and b.iszy=#{iszy}

      	select 
 			count(*) as cntamt,
 			sum(b.tranamt*c.percent) as totalamt
	 	from 
			t_account_base a
			inner join T_ACCOUNT_PERSON_PLEDGE b on a.accountid=b.accountid
 			left join t_manager_account c on a.accountid=c.accountid		
		where 1=1  and   c.managerid=#{managerid} 

			    and   b.startdate>=#{startdate}



			    	 and   b.enddate <=#{enddate}

select a.branchid as branchid, a.branchname as branchname, a.accountname as accountname, a.dqye as dqye, a.gendate as enddate, sum(a.inamt) as inamt, sum(a.outamt) as outamt, sum(a.netinout) as netinout from (select * from t_public_deposit_change where 1=1
and gendate>=#{startdate}
and gendate<=#{enddate}
and branchid=#{branchid}
and accountname like CONCAT(CONCAT('%',#{accountname}),'%')
and NETINOUT>=#{minnetamt}
order by gendate desc) a group by a.accountname order by a.branchid,a.gendate desc

select a.branchid as branchid, a.branchname as branchname, a.accountname as accountname, a.dqye as dqye, a.ncye as ncye, a.tolastday as tolastday, a.tolastyear as tolastyear, a.subcode as subcode, a.inamt as inamt, a.outamt as outamt, a.netinout as netinout, a.gendate as enddate from t_public_deposit_change a where 1=1
and a.branchid=#{branchid}
and a.accountname=#{accountname}
and gendate>=#{startdate}
and gendate<=#{enddate}
and NETINOUT>=#{minnetamt}
order by a.gendate desc

select a.accounttype, f.branchname, a.accountid, a.accountname, b.tranamt, b.startdate, b.enddate, b.channel, b.iszy, b.productbatch as productbatch, e.name as productname, g.name as channelname
,c.percent as percent
from
t_account_base a
inner join t_finance_detail b on a.accountid=b.accountid
left join t_manager_account c on a.accountid=c.accountid
left join sys_account d on c.managerid=d.accountid
left join t_finance_product_type e on b.productid=e.id
left join sys_branch f on d.branchid=f.branchid
left join t_enum g on g.type=1 and g.value=b.channel
where 1=1
and f.branchid=#{branchid}
and c.managerid=#{managerid}
and b.productid=#{productid}
and b.productbatch like CONCAT(CONCAT('%',#{productbatch}),'%')
and b.channel=#{channel}
and b.startdate>=#{startdate}
and b.enddate<=#{enddate}
and a.accounttype in
#{item}
b.iszy=#{iszy}
group by b.saleid

select a.gendate as balgendate, a.accountid as accountid , a.branchid, a.accountname, a.customno, a.subcode, a.bal as bal, a.yearavg , a.m1, a.m2, a.m3, a.m4, a.m5, a.m6, a.m7, a.m8, a.m9, a.m10, a.m11, a.m12, a.mrate, a.fdfs, a.fdbl, a.wjfl, a.acctstatus, c.branchname from
(select * from (select distinct accountid,gendate from t_accountbal_record where gendate<=#{gendate} order by gendate desc) f group by accountid order by gendate desc) g
left join t_accountbal_record a on a.accountid=g.accountid and a.gendate=g.gendate left join t_account_base d on a.accountid=d.accountid left join t_manager_account e on d.accountid=e.accountid left join sys_account f on e.managerid=f.accountid left join sys_branch c on d.branchid=c.branchid where 1=1
and a.accountid like CONCAT(CONCAT('%',#{accountid}),'%')
and a.accounttype =#{accounttype}
and f.branchid=#{branchid}
and f.accountid=#{managerid}
and a.accountname like CONCAT(CONCAT('%',#{accountname}),'%')
and a.customno like CONCAT(CONCAT('%',#{customno}),'%')
and a.property like CONCAT(CONCAT('%',#{property}),'%')
and a.subcode like CONCAT(CONCAT('%',#{subcode}),'%')
and a.acctstatus=#{status}
group by a.accountid
